sourcesContent:
import express from 'express';

import userModel from '../../models/userModel';

const router = express.Router();

/**
 * Route        /user/
 * Des          get all users
 * Params       none
 * Access       private
 * Method       GET
 **/

router.get('/', async (req, res) => {
   try {
      const users = await userModel.find();
      
      console.log(users);

      if (!users)
         res.status(400).json({
            status: 'fail',
            message: 'No users found',
         });

      res.status(200).json({
         status: 'success',
         message: 'Users found',
         data: users,
      });
   } catch (err) {
      res.status(500).json({
         status: 'fail',
         message: 'Something went wrong',
         error: err,
      });
   }
});

export default router;

